sp_widget.client_script:
function(spUtil, $scope, $timeout) {
    /* widget controller */
    var c = this;
		c.data.recordName=0;

    spUtil.recordWatch($scope, "x_166119_gsnow_game", "u_name=Game1", function(name, data) {
			c.data.recordName = c.data.recordName + 1;
	 }); 
}

