sp_widget.script:
(function() {
	var grMonster = new GlideRecord('x_166119_gsnow_monster');
	grMonster.get('u_name', "Script Includer");

	var imageID ='';
	var grImage = new GlideRecord('sys_attachment');
	grImage.addQuery('file_name','u_image');
	grImage.addQuery('table_sys_id',grMonster.sys_id.getDisplayValue());
	grImage.query();

	while(grImage.next()){
		imageID = grImage.sys_id.getDisplayValue();
	}

  data.monster = {
		"name": grMonster.u_name.getDisplayValue(),
		"image": imageID,
		"level": grMonster.u_level.getDisplayValue(),
		"reward_treasure": grMonster.u_reward_treasure.getDisplayValue(),
		"reward_level": grMonster.u_reward_level.getDisplayValue(),
		"short_description": grMonster.u_short_description.getDisplayValue()
	}
})();
