sp_widget.client_script:
function(spUtil, $scope, $timeout) {
    var c = this;
		c.data.allReady = false;

    spUtil.recordWatch($scope, "x_166119_gsnow_game", "number=${c.data.gameID}", function(name, data) {
			server.update();
	 });

		$scope.playerReady = function(player) {
		for(var i=0; i < c.data.players.length; i++){
			if(!c.data.players[i].ready && c.data.players[i].name == player.name){
				c.data.players[i].ready = true;
				c.data.readyPlayer += 1;
			}
			else if(c.data.players[i].ready && c.data.players[i].name == player.name){
				c.data.players[i].ready = false;
				c.data.readyPlayer -= 1;
			}
		}	
		if(c.data.readyPlayer >= c.data.maxPlayer){
			c.data.allReady = true;
		}
		else {
			c.data.allReady = false;
		}
	}
}

//{{c.data.allReady}}

sp_widget.script:
(function() {
	// Known Error: Cancel Game: Lösche M2M Einbtrag Player-Game
	// Niemand darf auf den ready button des anderen klicken
	// Laufleiste mit Servicenow Weißheiten
/*###########################################################################*/	
	data.gameID = "";
	data.players = [];
	data.maxPlayer = 0;
	data.readyPlayer = 0;

	// Get gameID from URL
	data.gameID = $sp.getParameter("gameID");

	// Get selected Game
	var grGame = new GlideRecord('x_166119_gsnow_game');
	grGame.get('number', data.gameID);
	data.maxPlayer = grGame.u_total_number_player.getDisplayValue();

	// Get all Players
	var grPlayer = new GlideRecord('x_166119_gsnow_player');
	grPlayer.addQuery('u_game', grGame.sys_id);
	grPlayer.query();

	while(grPlayer.next()){
		var player = {
			name: grPlayer.u_user.getDisplayValue(),
			ready: false,
			selected: false	
		};

		if(grPlayer.u_user == gs.getUserID()){
			player.selected = true;
		}
		data.players.push(player);
	}
})();


