sp_widget.client_script:
function(spUtil, $scope, $timeout) {
    var c = this;
		c.data.allReady = false;

	spUtil.recordWatch($scope, "x_166119_gsnow_game", "number=" + c.data.gameID, function(name, data) {
		if("u_actual_player" == name.data.changes){
			location.reload();
		}
		else if("40" == name.data.record.u_state.value){
			console.log("Open new page");
		}
		else{
			server.update();
		}
  });

		$scope.playerReady = function(player) {
			for(var i=0; i < c.data.players.length; i++){
				if(!c.data.players[i].ready && c.data.players[i].name == player.name){
					c.data.players[i].ready = true;
					c.data.readyPlayer =  parseInt(c.data.readyPlayer) + 1;
					c.data.readyClick = i+1;
					c.server.update();
				}
			}
		}

		$scope.startGame = function(){
			c.data.state = 30;
			c.server.update();
		}
}

